import requests

url_saml = "https://api17.sapsf.com/oauth/idp"

payload_saml='client_id=ZjM1ZmVlOTQxZmVhMDIzY2JlMTRlNjc0ZmZmZg&user_id=APPSAPCADF&token_url=https%3A%2F%2Fapi17.sapsf.com%2Fouth%2Ftoken&private_'
headers_saml = {
  'Content-Type': 'application/x-www-form-urlencoded'
}

response_saml = requests.request("POST", url_saml, headers=headers_saml, data=payload_saml)

print(response_saml.text)

url_token = "https://api17.sapsf.com/oauth/token"

payload_token='company_id=ontariopowP&client_id=ZjM1ZmVlOTQxZmVhMDIzY2JlMTRlNjc0ZmZmZg&grant_type=urn:ietf:params:oauth:grant-type:saml2-bearer&assertion='+response_saml.text
headers_token = {
  'Content-Type': 'application/x-www-form-urlencoded'
}

response_token = requests.request("POST", url_token, headers=headers_token, data=payload_token)
print('Token')
print(response_token.text)
